import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [filter, setFilter] = useState("all");

  // Load tasks from localStorage on mount
  useEffect(() => {
    const stored = localStorage.getItem("my-tasks");
    if (stored) {
      setTasks(JSON.parse(stored));
    }
  }, []);

  // Save tasks to localStorage on change
  useEffect(() => {
    localStorage.setItem("my-tasks", JSON.stringify(tasks));
  }, [tasks]);

  const addTask = () => {
    if (newTask.trim() === "") return;

    const newTodo = {
      id: Date.now(),
      text: newTask,
      done: false,
      isEditing: false,
    };

    setTasks([...tasks, newTodo]);
    setNewTask("");
  };

  const toggleDone = (id) => {
    const updated = tasks.map((task) =>
      task.id === id ? { ...task, done: !task.done } : task
    );
    setTasks(updated);
  };

  const deleteTask = (id) => {
    const updated = tasks.filter((task) => task.id !== id);
    setTasks(updated);
  };

  const startEditing = (id) => {
    const updated = tasks.map((task) =>
      task.id === id ? { ...task, isEditing: true } : task
    );
    setTasks(updated);
  };

  const handleEditChange = (id, e) => {
    const updated = tasks.map((task) =>
      task.id === id ? { ...task, text: e.target.value } : task
    );
    setTasks(updated);
  };

  const saveEdit = (id) => {
    const updated = tasks.map((task) =>
      task.id === id ? { ...task, isEditing: false } : task
    );
    setTasks(updated);
  };

  const clearCompleted = () => {
    const updated = tasks.filter((task) => !task.done);
    setTasks(updated);
  };

  const filteredTasks = tasks.filter((task) => {
    if (filter === "done") return task.done;
    if (filter === "undone") return !task.done;
    return true;
  });

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">üìù My To-Do List</h2>

      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Add a task..."
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          onKeyDown={(e) => {
            if (e.key === "Enter") addTask();
          }}
        />
        <button className="btn btn-primary" onClick={addTask}>
          Add
        </button>
      </div>

      <div className="btn-group mb-3">
        <button
          className={`btn btn-sm btn-outline-primary ${
            filter === "all" ? "active" : ""
          }`}
          onClick={() => setFilter("all")}
        >
          All
        </button>
        <button
          className={`btn btn-sm btn-outline-primary ${
            filter === "done" ? "active" : ""
          }`}
          onClick={() => setFilter("done")}
        >
          Completed
        </button>
        <button
          className={`btn btn-sm btn-outline-primary ${
            filter === "undone" ? "active" : ""
          }`}
          onClick={() => setFilter("undone")}
        >
          Incomplete
        </button>
      </div>

      <ul className="list-group mb-3">
        {filteredTasks.map((task) => (
          <li
            key={task.id}
            className={`list-group-item d-flex justify-content-between align-items-center ${
              task.done ? "list-group-item-success" : ""
            }`}
          >
            <div className="d-flex align-items-center w-100">
              <input
                type="checkbox"
                className="form-check-input me-2"
                checked={task.done}
                onChange={() => toggleDone(task.id)}
              />
              {task.isEditing ? (
                <input
                  type="text"
                  className="form-control me-2"
                  value={task.text}
                  onChange={(e) => handleEditChange(task.id, e)}
                  onKeyDown={(e) => {
                    if (e.key === "Enter") saveEdit(task.id);
                  }}
                />
              ) : (
                <span
                  style={{
                    textDecoration: task.done ? "line-through" : "none",
                  }}
                >
                  {task.text}
                </span>
              )}
            </div>
            <div>
              {task.isEditing ? (
                <button
                  className="btn btn-sm btn-success me-2"
                  onClick={() => saveEdit(task.id)}
                >
                  üíæ
                </button>
              ) : (
                <button
                  className="btn btn-sm btn-warning me-2"
                  onClick={() => startEditing(task.id)}
                >
                  ‚úèÔ∏è
                </button>
              )}
              <button
                className="btn btn-sm btn-danger"
                onClick={() => deleteTask(task.id)}
              >
                üóëÔ∏è
              </button>
            </div>
          </li>
        ))}
      </ul>

      <button className="btn btn-outline-danger" onClick={clearCompleted}>
        Clear Completed
      </button>
    </div>
  );
}

export default App;
