import React, { useEffect, useState } from "react";

function TaskPage() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");

  const token = localStorage.getItem("token");

  // Fetch tasks
  const fetchTasks = async () => {
    try {
      const res = await fetch("http://localhost:4000/tasks", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const data = await res.json();
      setTasks(data);
    } catch (error) {
      console.error("Failed to fetch tasks", error);
    }
  };

  useEffect(() => {
    fetchTasks();
  }, []);

  // Add task
  const addTask = async () => {
    if (!newTask.trim()) return;

    try {
      const res = await fetch("http://localhost:4000/tasks", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ text: newTask, done: false }),
      });

      if (!res.ok) throw new Error("Failed to add task");

      const task = await res.json();
      setTasks([...tasks, task]);
      setNewTask("");
    } catch (error) {
      console.error(error);
    }
  };

  // Toggle done
  const toggleDone = async (id, done) => {
    try {
      const res = await fetch(`http://localhost:4000/tasks/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ done: !done }),
      });

      if (!res.ok) throw new Error("Failed to update task");

      const updated = await res.json();
      setTasks(tasks.map((task) => (task.id === id ? updated : task)));
    } catch (error) {
      console.error(error);
    }
  };

  // Delete task
  const deleteTask = async (id) => {
    try {
      const res = await fetch(`http://localhost:4000/tasks/${id}`, {
        method: "DELETE",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      if (res.status !== 204) throw new Error("Failed to delete task");

      setTasks(tasks.filter((task) => task.id !== id));
    } catch (error) {
      console.error(error);
    }
  };

  // Edit task
  const editTask = (id, text) => {
    setTasks(tasks.map((task) => (task.id === id ? { ...task, text, isEditing: true } : task)));
  };

  const saveEdit = async (id) => {
    const task = tasks.find((task) => task.id === id);
    try {
      const res = await fetch(`http://localhost:4000/tasks/${id}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ text: task.text }),
      });

      if (!res.ok) throw new Error("Failed to update task");

      const updated = await res.json();
      setTasks(tasks.map((t) => (t.id === id ? { ...updated, isEditing: false } : t)));
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="container mt-5">
      <h2>To-Do App</h2>

      <div className="mb-3">
        <input
          className="form-control"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add a new task"
        />
        <button className="btn btn-primary mt-2" onClick={addTask}>
          Add Task
        </button>
      </div>

      <ul className="list-group">
        {tasks.map((task) => (
          <li
            key={task.id}
            className={`list-group-item d-flex justify-content-between align-items-center ${
              task.done ? "list-group-item-success" : ""
            }`}
          >
            <div>
              <input
                type="checkbox"
                checked={task.done}
                onChange={() => toggleDone(task.id, task.done)}
              />{" "}
              {task.isEditing ? (
                <input
                  value={task.text}
                  onChange={(e) =>
                    setTasks(
                      tasks.map((t) =>
                        t.id === task.id ? { ...t, text: e.target.value } : t
                      )
                    )
                  }
                />
              ) : (
                <span className="ms-2">{task.text}</span>
              )}
            </div>
            <div>
              {task.isEditing ? (
                <button className="btn btn-sm btn-success me-2" onClick={() => saveEdit(task.id)}>
                  Save
                </button>
              ) : (
                <button className="btn btn-sm btn-secondary me-2" onClick={() => editTask(task.id, task.text)}>
                  Edit
                </button>
              )}
              <button className="btn btn-sm btn-danger" onClick={() => deleteTask(task.id)}>
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TaskPage;
