// src/components/TaskPage.js
import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function TaskPage() {
  const [newTask, setNewTask] = useState('');
  const [filter, setFilter] = useState('all');
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
// Fetch tasks from backend on mount
useEffect(() => {
    async function fetchTasks() {
      setLoading(true);
      setError(null);
      try {
        const res = await fetch('http://localhost:4000/tasks');
        if (!res.ok) throw new Error(`Error: ${res.status}`);
        const data = await res.json();
        setTasks(data);
      } catch (err) {
        setError('Failed to load tasks');
      } finally {
        setLoading(false);
      }
    }
    fetchTasks();
  }, []);

  // Add new task
  const addTask = async () => {
    if (!newTask.trim()) return;
    setError(null);
    try {
      const res = await fetch('http://localhost:4000/tasks', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: newTask, done: false }),
      });
      if (!res.ok) throw new Error(`Error: ${res.status}`);
      const createdTask = await res.json();
      setTasks(prev => [...prev, createdTask]);
      setNewTask('');
    } catch {
      setError('Failed to add task');
    }
  };

  // Toggle task done status
  const toggleDone = async (id, done) => {
    setError(null);
    try {
      const res = await fetch(`http://localhost:4000/tasks/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ done: !done }),
      });
      if (!res.ok) throw new Error(`Error: ${res.status}`);
      const updatedTask = await res.json();
      setTasks(prev => prev.map(t => (t.id === id ? updatedTask : t)));
    } catch {
      setError('Failed to update task');
    }
  };

  // Delete task
  const deleteTask = async (id) => {
    setError(null);
    try {
      const res = await fetch(`http://localhost:4000/tasks/${id}`, { method: 'DELETE' });
      if (res.status !== 204) throw new Error(`Error: ${res.status}`);
      setTasks(prev => prev.filter(t => t.id !== id));
    } catch {
      setError('Failed to delete task');
    }
  };

  // Editing state stored per task
  const startEditing = (id) => {
    setTasks(tasks.map(t => (t.id === id ? { ...t, isEditing: true } : t)));
  };
  const handleEditChange = (id, text) => {
    setTasks(tasks.map(t => (t.id === id ? { ...t, text } : t)));
  };
  const saveEdit = async (id) => {
    const task = tasks.find(t => t.id === id);
    if (!task) return;
    setError(null);
    try {
      const res = await fetch(`http://localhost:4000/tasks/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ text: task.text }),
      });
      if (!res.ok) throw new Error(`Error: ${res.status}`);
      const updatedTask = await res.json();
      setTasks(tasks.map(t => (t.id === id ? { ...updatedTask, isEditing: false } : t)));
    } catch {
      setError('Failed to save edit');
    }
  };

  // Clear completed tasks
  const clearCompleted = () => {
    // You can extend to delete completed tasks from backend, here just filter frontend
    const incomplete = tasks.filter(t => !t.done);
    setTasks(incomplete);
  };

  // Filter tasks
  const filteredTasks = tasks.filter(task => {
    if (filter === 'done') return task.done;
    if (filter === 'undone') return !task.done;
    return true;
  });

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">📝 To-Do List</h1>

      {error && (
        <div className="alert alert-danger" role="alert">
          {error}
        </div>
      )}

      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Add a task..."
          value={newTask}
          onChange={e => setNewTask(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && addTask()}
          disabled={loading}
        />
        <button className="btn btn-primary" onClick={addTask} disabled={loading}>
          Add
        </button>
      </div>

      <div className="btn-group mb-3">
        <button
          className={`btn btn-sm btn-outline-primary ${filter === 'all' ? 'active' : ''}`}
          onClick={() => setFilter('all')}
        >
          All
        </button>
        <button
          className={`btn btn-sm btn-outline-primary ${filter === 'done' ? 'active' : ''}`}
          onClick={() => setFilter('done')}
        >
          Completed
        </button>
        <button
          className={`btn btn-sm btn-outline-primary ${filter === 'undone' ? 'active' : ''}`}
          onClick={() => setFilter('undone')}
        >
          Incomplete
        </button>
      </div>

      {loading ? (
        <p>Loading tasks...</p>
      ) : (
        <ul className="list-group">
          {filteredTasks.length === 0 && <li className="list-group-item">No tasks found.</li>}
          {filteredTasks.map(task => (
            <li
              key={task.id}
              className={`list-group-item d-flex justify-content-between align-items-center ${
                task.done ? 'list-group-item-success' : ''
              }`}
            >
              <div className="d-flex align-items-center w-100">
                {task.isEditing ? (
                  <input
                    type="text"
                    className="form-control me-2"
                    value={task.text}
                    onChange={e => handleEditChange(task.id, e.target.value)}
                    onKeyDown={e => {
                      if (e.key === 'Enter') saveEdit(task.id);
                    }}
                  />
                ) : (
                  <span
                    className="me-2 flex-grow-1"
                    style={{ cursor: 'pointer', textDecoration: task.done ? 'line-through' : 'none' }}
                    onClick={() => toggleDone(task.id, task.done)}
                  >
                    {task.text}
                  </span>
                )}

                {task.isEditing ? (
                  <button className="btn btn-success btn-sm me-1" onClick={() => saveEdit(task.id)}>
                    💾
                  </button>
                ) : (
                  <button className="btn btn-secondary btn-sm me-1" onClick={() => startEditing(task.id)}>
                    ✏️
                  </button>
                )}

                <button className="btn btn-danger btn-sm" onClick={() => deleteTask(task.id)}>
                  🗑️
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}

      {tasks.some(task => task.done) && (
        <div className="mt-3 text-end">
          <button className="btn btn-outline-danger btn-sm" onClick={clearCompleted}>
            Clear Completed Tasks
          </button>
        </div>
      )}
    </div>
  );
}
