import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [newTask, setNewTask] = useState("");
  const [filter, setFilter] = use

  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem("my-tasks");
    if (saved) {
      try {
        return JSON.parse(saved);
      } catch (e) {
        console.error("Error parsing localStorage:", e);
      }
    }
    return [];
  });

  useEffect(() => {
    localStorage.setItem("my-tasks", JSON.stringify(tasks));
  }, [tasks]);

  const addTask = () => {
    if (newTask.trim() === "") return;
    setTasks([...tasks, { text: newTask, done: false, isEditing: false }]);
    setNewTask("");
  };

  const toggleDone = (index) => {
    const updated = tasks.map((task, i) =>
      i === index ? { ...task, done: !task.done } : task
    );
    setTasks(updated);
  };

  const deleteTask = (index) => {
    const updated = tasks.filter((_, i) => i !== index);
    setTasks(updated);
  };

  const startEditing = (index) => {
    const updated = tasks.map((task, i) =>
      i === index ? { ...task, isEditing: true } : task
    );
    setTasks(updated);
  };

  const handleEditChange = (index, newText) => {
    const updated = tasks.map((task, i) =>
      i === index ? { ...task, text: newText } : task
    );
    setTasks(updated);
  };

  const saveEdit = (index) => {
    const updated = tasks.map((task, i) =>
      i === index ? { ...task, isEditing: false } : task
    );
    setTasks(updated);
  };

  const clearCompleted = () => {
    const updated = tasks.filter((task) => !task.done);
    setTasks(updated);
  };


  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">📝 To-Do List</h1>

      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add a task..."
          onKeyDown = {(e) => {
            if(e.key === 'Enter') {
              addTask();
            }
          }}

        />
        <button className="btn btn-primary" onClick={addTask}>
          Add
        </button>
      </div>

      <ul className="list-group">
        {tasks.map((task, index) => (
          <li
            key={index}
            className={`list-group-item d-flex justify-content-between align-items-center ${
              task.done ? 'list-group-item-success' : ''
            }`}
          >
            <div className="d-flex align-items-center w-100">
              {task.isEditing ? (
                <input
                  className="form-control me-2"
                  value={task.text}
                  onChange={(e) => handleEditChange(index, e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === "Enter") saveEdit(index);
                  }}
                />
              ) : (
                <span
                  className="me-2 flex-grow-1"
                  onClick={() => toggleDone(index)}
                  style={{
                    cursor: "pointer",
                    textDecoration: task.done ? "line-through" : "none"
                  }}
                >
                  {task.text}
                </span>
              )}

              {task.isEditing ? (
                <button className="btn btn-success btn-sm me-1" onClick={() => saveEdit(index)}>
                  💾
                </button>
              ) : (
                <button className="btn btn-secondary btn-sm me-1" onClick={() => startEditing(index)}>
                  ✏️
                </button>
              )}

              <button className="btn btn-danger btn-sm" onClick={() => deleteTask(index)}>
                🗑️
              </button>
            </div>
          </li>
        ))}
      </ul>


      {tasks.some(task => task.done) && (
        <div className="mt-3 text-end">
          <button className="btn btn-outline-danger btn-sm" onClick={clearCompleted}>
            Clear Completed Tasks
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
