import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function TaskPage() {
  const [tasks, setTasks] = useState([]);
  const [text, setText] = useState("");
  const [editId, setEditId] = useState(null);       // task id currently editing
  const [editText, setEditText] = useState("");     // edit input value
  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) return navigate("/login");
    fetch("http://localhost:4000/tasks", {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => res.json()).then(setTasks);
  }, [token, navigate]);

  const addTask = async () => {
    if (!text) return;
    const res = await fetch("http://localhost:4000/tasks", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({ text })
    });

    if (res.ok) {
      const task = await res.json();
      setTasks([...tasks, task]);
      setText("");
    }
  };

  const toggleTask = async (id, done) => {
    await fetch(`http://localhost:4000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({ done: !done })
    });
    setTasks(tasks.map(t => t.id === id ? { ...t, done: !done } : t));
  };

  const deleteTask = async (id) => {
    await fetch(`http://localhost:4000/tasks/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` }
    });
    setTasks(tasks.filter(t => t.id !== id));
  };

  // Save edit
  const saveEdit = async (id) => {
    if (!editText.trim()) return;
    await fetch(`http://localhost:4000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`
      },
      body: JSON.stringify({ text: editText })
    });
    setTasks(tasks.map(t => t.id === id ? { ...t, text: editText } : t));
    setEditId(null);
  };

  return (
    <div className="container mt-5">
      <h2>To-Do List</h2>
      <div className="input-group mb-3">
        <input
          value={text}
          onChange={e => setText(e.target.value)}
          className="form-control"
          placeholder="New task..."
        />
        <button className="btn btn-primary" onClick={addTask}>Add</button>
      </div>

      <ul className="list-group">
        {tasks.map(task => (
          <li key={task.id} className="list-group-item d-flex justify-content-between align-items-center">
            {editId === task.id ? (
              <>
                <input
                  value={editText}
                  onChange={e => setEditText(e.target.value)}
                  className="form-control me-2"
                />
                <button className="btn btn-success btn-sm me-1" onClick={() => saveEdit(task.id)}>Save</button>
                <button className="btn btn-secondary btn-sm" onClick={() => setEditId(null)}>Cancel</button>
              </>
            ) : (
              <>
                <span
                  style={{ textDecoration: task.done ? "line-through" : "none", cursor: "pointer" }}
                  onClick={() => toggleTask(task.id, task.done)}
                >
                  {task.text}
                </span>
                <div>
                  <button className="btn btn-warning btn-sm me-1" onClick={() => {
                    setEditId(task.id);
                    setEditText(task.text);
                  }}>
                    Edit
                  </button>
                  <button className="btn btn-danger btn-sm" onClick={() => deleteTask(task.id)}>Delete</button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TaskPage;
