import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function TaskPage() {
  const [tasks, setTasks] = useState([]);
  const [text, setText] = useState("");
  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) return navigate("/login");
    fetch("http://localhost:4000/tasks", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then(setTasks);
  }, [token, navigate]);

  const addTask = async () => {
    if (!text.trim()) return;
    const res = await fetch("http://localhost:4000/tasks", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ text }),
    });
    if (res.ok) {
      const task = await res.json();
      setTasks([...tasks, task]);
      setText("");
    }
  };

  const toggleTask = async (id, done) => {
    await fetch(`http://localhost:4000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ done: !done }),
    });
    setTasks(tasks.map((t) => (t.id === id ? { ...t, done: !done } : t)));
  };

  const deleteTask = async (id) => {
    await fetch(`http://localhost:4000/tasks/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(tasks.filter((t) => t.id !== id));
  };

  const saveEdit = async (id, newText) => {
    if (!newText.trim()) return;
    await fetch(`http://localhost:4000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ text: newText }),
    });
    setTasks(tasks.map((t) => (t.id === id ? { ...t, text: newText } : t)));
  };

  // TaskItem component inside TaskPage for edit functionality
  const TaskItem = ({ task }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [editText, setEditText] = useState(task.text);

    return (
      <li className="list-group-item d-flex justify-content-between align-items-center">
        {isEditing ? (
          <>
            <input
              className="form-control me-2"
              value={editText}
              onChange={(e) => setEditText(e.target.value)}
            />
            <button
              className="btn btn-success btn-sm me-1"
              onClick={() => {
                saveEdit(task.id, editText);
                setIsEditing(false);
              }}
            >
              Save
            </button>
            <button
              className="btn btn-secondary btn-sm"
              onClick={() => setIsEditing(false)}
            >
              Cancel
            </button>
          </>
        ) : (
          <>
            <span
              style={{
                textDecoration: task.done ? "line-through" : "none",
                cursor: "pointer",
                flexGrow: 1,
              }}
              onClick={() => toggleTask(task.id, task.done)}
            >
              {task.text}
            </span>
            <div>
              <button
                className="btn btn-warning btn-sm me-1"
                onClick={() => {
                  setIsEditing(true);
                  setEditText(task.text);
                }}
              >
                Edit
              </button>
              <button
                className="btn btn-danger btn-sm"
                onClick={() => deleteTask(task.id)}
              >
                Delete
              </button>
            </div>
          </>
        )}
      </li>
    );
  };

  return (
    <div className="container mt-5">
      <h2>To-Do List</h2>
      <div className="input-group mb-3">
        <input
          value={text}
          onChange={(e) => setText(e.target.value)}
          className="form-control"
          placeholder="New task..."
        />
        <button className="btn btn-primary" onClick={addTask}>
          Add
        </button>
      </div>

      <h4>Pending Tasks</h4>
      <ul className="list-group mb-4">
        {tasks.filter((t) => !t.done).length === 0 && (
          <li className="list-group-item text-muted">No pending tasks</li>
        )}
        {tasks
          .filter((t) => !t.done)
          .map((task) => (
            <TaskItem key={task.id} task={task} />
          ))}
      </ul>

      <h4>Completed Tasks</h4>
      <ul className="list-group">
        {tasks.filter((t) => t.done).length === 0 && (
          <li className="list-group-item text-muted">No completed tasks</li>
        )}
        {tasks
          .filter((t) => t.done)
          .map((task) => (
            <TaskItem key={task.id} task={task} />
          ))}
      </ul>
    </div>
  );
}

export default TaskPage;
