import React, { useState } from 'react';

function App() {
  const [newTask, setNewTask] = useState(""); // For current input
  const [tasks, setTasks] = useState([]);     // For all added tasks

  return (
    <div style={{ padding: "20px" }}>
      <h1>My To-Do App</h1>

      <input
        type="text"
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
        placeholder="Add a new task"
      />
      <button onClick={() => {
        if (newTask.trim() === "") return; // Avoid empty tasks
        setTasks([...tasks, newTask]);     // Add to tasks array
        setNewTask("");                    // Clear input
      }}>
        Add
      </button>

      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            {task}

            </li> // Display each task
        ))}
      </ul>
    </div>
  );
}

export default App;
