import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './TaskPage.css';

function TaskPage() {
  const [tasks, setTasks] = useState([]);
  const [text, setText] = useState('');
  const [filter, setFilter] = useState('all');
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }
    fetch('http://localhost:4000/tasks', {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then(res => res.json())
      .then(setTasks)
      .catch(console.error);
  }, [token, navigate]);

  const addTask = async () => {
    if (!text.trim()) return;

    const res = await fetch('http://localhost:4000/tasks', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ text }),
    });

    if (res.ok) {
      const task = await res.json();
      setTasks([...tasks, task]);
      setText('');
    }
  };

  const toggleTask = async (id, done) => {
    await fetch(`http://localhost:4000/tasks/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ done: !done }),
    });
    setTasks(tasks.map(t => (t.id === id ? { ...t, done: !done } : t)));
  };

  const deleteTask = async id => {
    await fetch(`http://localhost:4000/tasks/${id}`, {
      method: 'DELETE',
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(tasks.filter(t => t.id !== id));
  };

  const filteredTasks = tasks.filter(task => {
    if (filter === 'completed') return task.done;
    if (filter === 'pending') return !task.done;
    return true;
  });

  return (
    <div className="container taskpage-container">
      <h2 className="taskpage-header">My To-Do List</h2>

      <div className="input-group input-group-custom">
        <input
          type="text"
          className="form-control"
          placeholder="Add a new task..."
          value={text}
          onChange={e => setText(e.target.value)}
          onKeyDown={e => { if (e.key === 'Enter') addTask(); }}
        />
        <button className="btn btn-primary" onClick={addTask}>
          Add
        </button>
      </div>

      <ul className="nav nav-tabs nav-tabs-custom">
        {['all', 'completed', 'pending'].map(f => (
          <li className="nav-item" key={f}>
            <button
              className={`nav-link ${filter === f ? 'active' : ''}`}
              onClick={() => setFilter(f)}
              style={{ cursor: 'pointer' }}
            >
              {f.charAt(0).toUpperCase() + f.slice(1)}
            </button>
          </li>
        ))}
      </ul>

      <ul className="list-group shadow-sm">
        {filteredTasks.length === 0 ? (
          <li className="list-group-item text-center text-muted">
            No tasks to show
          </li>
        ) : (
          filteredTasks.map(task => (
            <li
              key={task.id}
              className={`list-group-item list-group-item-custom ${
                task.done ? 'list-group-item-completed' : ''
              }`}
            >
              <span
                onClick={() => toggleTask(task.id, task.done)}
                className="task-text"
              >
                {task.text}
              </span>
              <button
                onClick={() => deleteTask(task.id)}
                className="btn btn-sm btn-danger delete-btn"
                title="Delete task"
              >
                &times;
              </button>
            </li>
          ))
        )}
      </ul>
    </div>
  );
}

export default TaskPage;
