import React, { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [filter, setFilter] = useState("all");

  // Load tasks from localStorage
  useEffect(() => {
    const stored = localStorage.getItem("my-tasks");
    if (stored) setTasks(JSON.parse(stored));
  }, []);

  // Save to localStorage
  useEffect(() => {
    localStorage.setItem("my-tasks", JSON.stringify(tasks));
  }, [tasks]);

  const addTask = () => {
    if (newTask.trim() === "") return;

    const newTodo = {
      id: Date.now(),
      text: newTask,
      done: false,
      isEditing: false,
    };

    setTasks([...tasks, newTodo]);
    setNewTask("");
  };

  const toggleDone = (id) => {
    const updated = tasks.map((task) =>
      task.id === id ? { ...task, done: !task.done } : task
    );
    setTasks(updated);
  };

  const deleteTask = (id) => {
    const updated = tasks.filter((task) => task.id !== id);
    setTasks(updated);
  };

  const startEditing = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, isEditing: true } : task
      )
    );
  };

  const handleEditChange = (id, e) => {
    const updated = tasks.map((task) =>
      task.id === id ? { ...task, text: e.target.value } : task
    );
    setTasks(updated);
  };

  const saveEdit = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, isEditing: false } : task
      )
    );
  };

  const clearCompleted = () => {
    const updated = tasks.filter((task) => !task.done);
    setTasks(updated);
  };

  const filteredTasks = tasks.filter((task) => {
    if (filter === "done") return task.done;
    if (filter === "undone") return !task.done;
    return true;
  });

  return (
    <div className="min-vh-100 d-flex justify-content-center align-items-start bg-light py-5">
      <div className="card shadow-sm p-4 rounded-4" style={{ width: "100%", maxWidth: "600px" }}>
        <h3 className="text-center mb-4">📋 My To-Do List</h3>

        {/* Input */}
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control rounded-start"
            placeholder="Add a new task..."
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            onKeyDown={(e) => {
              if (e.key === "Enter") addTask();
            }}
          />
          <button className="btn btn-primary rounded-end" onClick={addTask}>
            Add
          </button>
        </div>

        {/* Filters */}
        <div className="btn-group w-100 mb-3">
          <button
            className={`btn btn-outline-secondary ${filter === "all" ? "active" : ""}`}
            onClick={() => setFilter("all")}
          >
            All
          </button>
          <button
            className={`btn btn-outline-secondary ${filter === "done" ? "active" : ""}`}
            onClick={() => setFilter("done")}
          >
            Completed
          </button>
          <button
            className={`btn btn-outline-secondary ${filter === "undone" ? "active" : ""}`}
            onClick={() => setFilter("undone")}
          >
            Incomplete
          </button>
        </div>

        {/* Tasks */}
        <ul className="list-group mb-3">
          {filteredTasks.length === 0 && (
            <li className="list-group-item text-center text-muted">No tasks to show</li>
          )}

          {filteredTasks.map((task) => (
            <li
              key={task.id}
              className={`list-group-item d-flex justify-content-between align-items-center ${
                task.done ? "bg-light text-muted" : ""
              }`}
            >
              <div className="d-flex align-items-center w-100">
                <input
                  type="checkbox"
                  className="form-check-input me-2"
                  checked={task.done}
                  onChange={() => toggleDone(task.id)}
                />
                {task.isEditing ? (
                  <input
                    type="text"
                    className="form-control me-2"
                    value={task.text}
                    onChange={(e) => handleEditChange(task.id, e)}
                    onKeyDown={(e) => {
                      if (e.key === "Enter") saveEdit(task.id);
                    }}
                  />
                ) : (
                  <span
                    style={{
                      textDecoration: task.done ? "line-through" : "none",
                      flex: 1,
                    }}
                  >
                    {task.text}
                  </span>
                )}
              </div>

              <div className="ms-2 d-flex gap-1">
                {task.isEditing ? (
                  <button className="btn btn-sm btn-success" onClick={() => saveEdit(task.id)}>
                    💾
                  </button>
                ) : (
                  <button className="btn btn-sm btn-outline-warning" onClick={() => startEditing(task.id)}>
                    ✏️
                  </button>
                )}
                <button className="btn btn-sm btn-outline-danger" onClick={() => deleteTask(task.id)}>
                  🗑️
                </button>
              </div>
            </li>
          ))}
        </ul>

        {/* Clear completed */}
        <button className="btn btn-outline-danger w-100" onClick={clearCompleted}>
          🧹 Clear Completed
        </button>
      </div>
    </div>
  );
}

export default App;
