import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [newTask, setNewTask] = useState("");
  const [filter, setFilter] = useState("all");

  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem("my-tasks");
    if (saved) {
      try {
        return JSON.parse(saved);
      } catch (e) {
        console.error("Error parsing localStorage:", e);
      }
    }
    return [];
  });

  useEffect(() => {
    fetch("/tasks")
      .then(res => res.json())
      .then(data => setTasks(data))
      .catch(err => console.error("Failed to fetch tasks:", err));
  }, []);

  const addTask = async (taskText) => {
    if (!taskText.trim()) return; // ignore empty tasks

    const res = await fetch("/tasks", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ text: taskText, done: false }),
    });

    const newTask = await res.json();
    setTasks(prev => [...prev, newTask]);
  };

  const toggleDone = async (id) => {
    const task = tasks.find(t => t.id === id);
    if (!task) return;

    const res = await fetch(`/tasks/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ done: !task.done }),
    });
    const updated = await res.json();
    setTasks(prev =>
      prev.map(t => (t.id === updated.id ? updated : t))
    );
  };

  const deleteTask = async (id) => {
    await fetch(`/tasks/${id}`, { method: "DELETE" });
    setTasks(prev => prev.filter(t => t.id !== id));
  };

  const startEditing = (id) => {
    const updated = tasks.map((task) =>
      task.id === id ? { ...task, isEditing: true } : task
    );
    setTasks(updated);
  };

  const handleEditChange = (id, newText) => {
    const updated = tasks.map((task) =>
      task.id === id ? { ...task, text: newText } : task
    );
    setTasks(updated);
  };

  const saveEdit = async (id) => {
    const task = tasks.find(t => t.id === id);
    if (!task) return;

    const res = await fetch(`/tasks/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ text: task.text }),
    });
    const updated = await res.json();

    const updatedTasks = tasks.map(t =>
      t.id === updated.id ? { ...updated, isEditing: false } : t
    );
    setTasks(updatedTasks);
  };

  const clearCompleted = () => {
    const updated = tasks.filter((task) => !task.done);
    setTasks(updated);
    // Optionally, you can add API calls to delete completed tasks from backend
  };

  const filteredTasks = (() => {
    switch (filter) {
      case "done":
        return tasks.filter((task) => task.done);
      case "undone":
        return tasks.filter((task) => !task.done);
      default:
        return tasks;
    }
  })();

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">📝 To-Do List</h1>

      <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="Add a task..."
          onKeyDown={(e) => {
            if (e.key === 'Enter' && newTask.trim()) {
              addTask(newTask);
              setNewTask("");
            }
          }}
        />
        <button
          className="btn btn-primary"
          onClick={() => {
            if (newTask.trim()) {
              addTask(newTask);
              setNewTask("");
            }
          }}
        >
          Add
        </button>
      </div>

      <div className="btn-group mb-3">
        <button
          className={`btn btn-sm btn-outline-primary ${filter === "all" ? "active" : ""}`}
          onClick={() => setFilter("all")}
        >
          All
        </button>
        <button
          className={`btn btn-sm btn-outline-primary ${filter === "done" ? "active" : ""}`}
          onClick={() => setFilter("done")}
        >
          Completed
        </button>
        <button
          className={`btn btn-sm btn-outline-primary ${filter === "undone" ? "active" : ""}`}
          onClick={() => setFilter("undone")}
        >
          Incomplete
        </button>
      </div>

      <ul className="list-group">
        {filteredTasks.map((task) => (
          <li
            key={task.id}
            className={`list-group-item d-flex justify-content-between align-items-center ${
              task.done ? 'list-group-item-success' : ''
            }`}
          >
            <div className="d-flex align-items-center w-100">
              {task.isEditing ? (
                <input
                  className="form-control me-2"
                  value={task.text}
                  onChange={(e) => handleEditChange(task.id, e.target.value)}
                  onKeyDown={(e) => {
                    if (e.key === "Enter") saveEdit(task.id);
                  }}
                />
              ) : (
                <span
                  className="me-2 flex-grow-1"
                  onClick={() => toggleDone(task.id)}
                  style={{
                    cursor: "pointer",
                    textDecoration: task.done ? "line-through" : "none"
                  }}
                >
                  {task.text}
                </span>
              )}

              {task.isEditing ? (
                <button className="btn btn-success btn-sm me-1" onClick={() => saveEdit(task.id)}>
                  💾
                </button>
              ) : (
                <button className="btn btn-secondary btn-sm me-1" onClick={() => startEditing(task.id)}>
                  ✏️
                </button>
              )}

              <button className="btn btn-danger btn-sm" onClick={() => deleteTask(task.id)}>
                🗑️
              </button>
            </div>
          </li>
        ))}
      </ul>

      {tasks.some(task => task.done) && (
        <div className="mt-3 text-end">
          <button className="btn btn-outline-danger btn-sm" onClick={clearCompleted}>
            Clear Completed Tasks
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
