import React, { useState, useEffect } from "react";
import './TaskPage.css';


function TaskPage() {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState("pending"); // "pending", "completed", or "all"
  const [editingId, setEditingId] = useState(null);
  const [editingText, setEditingText] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) return; // Add navigation to login if needed
    fetch("http://localhost:4000/tasks", {
      headers: { Authorization: `Bearer ${token}` },
    })
      .then((res) => res.json())
      .then(setTasks);
  }, [token]);

  const filteredTasks = tasks.filter((task) => {
    if (filter === "pending") return !task.done;
    if (filter === "completed") return task.done;
    return true; // all
  });

  const toggleTask = async (id, done) => {
    await fetch(`http://localhost:4000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ done: !done }),
    });
    setTasks(tasks.map((t) => (t.id === id ? { ...t, done: !done } : t)));
  };

  const deleteTask = async (id) => {
    await fetch(`http://localhost:4000/tasks/${id}`, {
      method: "DELETE",
      headers: { Authorization: `Bearer ${token}` },
    });
    setTasks(tasks.filter((t) => t.id !== id));
  };

  const startEditing = (task) => {
    setEditingId(task.id);
    setEditingText(task.text);
  };

  const saveEdit = async (id) => {
    if (!editingText.trim()) return;
    await fetch(`http://localhost:4000/tasks/${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify({ text: editingText }),
    });
    setTasks(
      tasks.map((t) => (t.id === id ? { ...t, text: editingText } : t))
    );
    setEditingId(null);
  };

  const cancelEdit = () => {
    setEditingId(null);
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4">To-Do List</h2>

      {/* Filter Tabs */}
      <div className="mb-3 d-flex justify-content-center gap-3">
        {["pending", "completed", "all"].map((f) => (
          <button
            key={f}
            className={`btn ${
              filter === f ? "btn-primary" : "btn-outline-primary"
            }`}
            onClick={() => setFilter(f)}
          >
            {f.charAt(0).toUpperCase() + f.slice(1)}
          </button>
        ))}
      </div>

      {/* Tasks List */}
      <ul className="list-group shadow-sm rounded">
        {filteredTasks.length === 0 && (
          <li className="list-group-item text-center text-muted">
            No tasks here.
          </li>
        )}
        {filteredTasks.map((task) => (
          <li
            key={task.id}
            className={`list-group-item d-flex justify-content-between align-items-center ${
              task.done ? "list-group-item-success" : ""
            }`}
          >
            {/* Editable Text */}
            {editingId === task.id ? (
              <input
                type="text"
                className="form-control me-3"
                value={editingText}
                onChange={(e) => setEditingText(e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === "Enter") saveEdit(task.id);
                  if (e.key === "Escape") cancelEdit();
                }}
                autoFocus
              />
            ) : (
              <span
                style={{
                  textDecoration: task.done ? "line-through" : "none",
                  cursor: "pointer",
                  flex: 1,
                }}
                onClick={() => toggleTask(task.id, task.done)}
                title="Click to toggle done"
              >
                {task.text}
              </span>
            )}

            {/* Buttons */}
            <div className="d-flex gap-2">
              {editingId === task.id ? (
                <>
                  <button
                    className="btn btn-sm btn-success"
                    onClick={() => saveEdit(task.id)}
                  >
                    Save
                  </button>
                  <button
                    className="btn btn-sm btn-secondary"
                    onClick={cancelEdit}
                  >
                    Cancel
                  </button>
                </>
              ) : (
                <>
                  <button
                    className="btn btn-sm btn-warning"
                    onClick={() => startEditing(task)}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-sm btn-danger"
                    onClick={() => deleteTask(task.id)}
                  >
                    Delete
                  </button>
                </>
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TaskPage;
